import React, { useState } from 'react';
import axios from 'axios';
import '../css/ForgotPassword.css'

const ForgotPassword = () => {
  const [email, setEmail] = useState('');
  const [otp, setOtp] = useState('');
  const [newPassword, setNewPassword] = useState(''); // New password field
  const [confirmPassword, setConfirmPassword] = useState(''); // Confirm password field
  const [resetSuccess, setResetSuccess] = useState(false);
  const [resetError, setResetError] = useState('');
  const [otpSent, setOtpSent] = useState(false);
  const [otpVerified, setOtpVerified] = useState(false);
  const apiUrl = process.env.REACT_APP_API_BASE_URL;

  const handleSendOTP = async () => {
    try {
      // Send a request to the server to send an OTP to the provided email
      const response = await axios.post(`${apiUrl}/verifyEmail`, { email });

      if (response.data === 'OTP sent successfully') {
        setOtpSent(true);
        setResetSuccess(false);
        setResetError('');
      } else {
        setOtpSent(false);
        setOtpVerified(false);
        setResetError('OTP request failed. Please try again later.');
      }
    } catch (error) {
      console.error('Error sending OTP:', error);
      setOtpSent(false);
      setOtpVerified(false);
      setResetError('An error occurred. Please try again later.');
    }
  };

  const handleVerifyOTP = async () => {
    try {
      // Send a request to the server to verify the OTP
      const response = await axios.post(`${apiUrl}/verify-otp`, { email, otp });

      if (response.data === 'OTP verified successfully') {
        setOtpVerified(true);
        setResetError('');
      } else {
        setOtpVerified(false);
        setResetError('OTP verification failed. Please enter a valid OTP.');
      }
    } catch (error) {
      console.error('Error verifying OTP:', error);
      setOtpVerified(false);
      setResetError('An error occurred. Please try again later.');
    }
  };

  const handleResetPassword = async () => {
    try {
      // Send a request to the server to reset the password with the new password
      const response = await axios.post(`${apiUrl}/reset-password`, {
        email,
        password,
        
      });

      if (response.data === 'success') {
        setResetSuccess(true);
        setResetError('');
      } else {
        setResetSuccess(false);
        setResetError('Password reset failed. Please try again later.');
      }
    } catch (error) {
      console.error('Error resetting password:', error);
      setResetSuccess(false);
      setResetError('An error occurred. Please try again later.');
    }
  };

  return (
    <div className="forgot-password-container">
      <div className="forgot-password-content">
        {resetSuccess ? (
          <div className="success-message">
            Password reset request sent successfully. Check your email for further instructions.
          </div>
        ) : (
          <div>
            <h2>Forgot Password</h2>
            <input
              type="email"
              placeholder="Enter your Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            {otpSent ? (
              otpVerified ? (
                <div>
                 
                  <input
                    type="password"
                    placeholder="New Password"
                    value={newPassword}
                    onChange={(e) => setNewPassword(e.target.value)}
                  />
                  <input
                    type="password"
                    placeholder="Confirm New Password"
                    value={confirmPassword}
                    onChange={(e) => setConfirmPassword(e.target.value)}
                  />
                  <button onClick={handleResetPassword}>Reset Password</button>
                  <p style={{ color: 'green' }}>OTP verified successfully!</p>
                </div>
              ) : (
                <div>
                  <input
                    type="text"
                    placeholder="Enter OTP"
                    value={otp}
                    onChange={(e) => setOtp(e.target.value)}
                  />
                  <button type="button" onClick={handleVerifyOTP}>Verify OTP</button>
                </div>
              )
            ) : (
              <button type="button" onClick={handleSendOTP}>Send OTP</button>
            )}
            {resetError && <div className="error-message">{resetError}</div>}
          </div>
        )}
      </div>
    </div>
  );
};

export default ForgotPassword;
