import React, { useState } from 'react';
import axios from 'axios';

const PdfUpload = () => {
  const [pdfFile, setPdfFile] = useState(null);
  const [uploadMessage, setUploadMessage] = useState('');

  const handleFileChange = (e) => {
    setPdfFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (pdfFile) {
      const formData = new FormData();
      formData.append('pdfFile', pdfFile);

      try {
        const response = await axios.post('http://localhost:8080/upload-pdf', formData, {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        });

        setUploadMessage('PDF uploaded and converted to JSON successfully.');
        console.log('Response:', response.data);
      } catch (error) {
        setUploadMessage('Error uploading PDF.');
        console.error('Error:', error);
      }
    } else {
      setUploadMessage('Please choose a PDF file to upload.');
    }
  };

  return (
    <div>
      
      <input type="file" accept=".pdf" onChange={handleFileChange} />
      <button type="button" onClick={handleUpload}>Upload and Convert</button>
      {uploadMessage && <div>{uploadMessage}</div>}
    </div>
  );
};

export default PdfUpload;
