import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css'; // Import the CSS file for styling

const Navbar = () => {
  const [showJobseekerMenu, setShowJobseekerMenu] = useState(false);
  const [showInterviewerMenu, setShowInterviewerMenu] = useState(false);

  const handleJobseekerHover = () => {
    setShowJobseekerMenu(true);
  };

  const handleJobseekerLeave = () => {
    setShowJobseekerMenu(false);
  };

  const handleInterviewerHover = () => {
    setShowInterviewerMenu(true);
  };

  const handleInterviewerLeave = () => {
    setShowInterviewerMenu(false);
  };

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <h3 className="navbar-title">Talent Tracker Application</h3>
      </div>
      <ul className="navbar-list">
        <li className="navbar-item">
          <Link to="/" className="navbar-link">Home</Link>
        </li>
        <li
          className="navbar-item"
          onMouseEnter={handleJobseekerHover}
          onMouseLeave={handleJobseekerLeave}
        >
          <span className="navbar-link">Jobseekers</span>
          {showJobseekerMenu && (
            <ul className="sub-menu">
              <li className="navbar-item">
                <Link to="/jobseeker/login" className="navbar-link">Login</Link>
              </li>
              <li className="navbar-item">
                <Link to="/jobseeker/register" className="navbar-link">Register</Link>
              </li>
            </ul>
          )}
        </li>
        <li
          className="navbar-item"
          onMouseEnter={handleInterviewerHover}
          onMouseLeave={handleInterviewerLeave}
        >
          <span className="navbar-link">Interviewers</span>
          {showInterviewerMenu && (
            <ul className="sub-menu">
              <li className="navbar-item">
                <Link to="/interviewer/login" className="navbar-link">Login</Link>
              </li>
              <li className="navbar-item">
                <Link to="/interviewer/register" className="navbar-link">Register</Link>
              </li>
            </ul>
          )}
        </li>
      </ul>
    </nav>
  );
};

export default Navbar;
