import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../css/LoginForm.css';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('user'); // Default role is 'user'
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const isFormValid = () => {
    if (!email.trim() || !password.trim()) {
      return false; // Username and password should not be empty or whitespace only
    }
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!isFormValid()) {
      window.alert('Please enter both username and password.');
      return;
    }
    try {

      let loginEndpoint;
      let count;
    if (email === 'admin' && password === 'admin') {
       count=0;
      loginEndpoint = 'http://localhost:8080/adminlogin'; // User login endpoint
    } else {
      count=1;
      loginEndpoint = 'http://localhost:8080/login'; // Admin login endpoint
    }
      // Replace 'http://localhost:5000' with your actual Spring Boot backend URL
      console.log('Email:', email);
      const response = await axios.post(loginEndpoint, {
        email,
        password,
        role, // Include the selected role in the request
      });

      setErrorMessage('');

      // Handle the response from the server (e.g., show a success message)
      console.log('Login successful', response.data);
      // Show a window alert for successful registration
      window.alert('Login successful!');

      // Use the navigate function to navigate to the home screen
      if(count ===0){
        navigate('/admin');
      }else{
        navigate('/home');
      }
      navigate('/home');

    } catch (error) {
      setErrorMessage('Login failed. Please try again later.');
      console.error('Login failed', error);
    }
  };

  return (
    <div className="login-form-container">
      
      <form className="login-form" onSubmit={handleSubmit}>
        <h2>Login</h2>
        {/* Radio buttons for role selection */}
        <div className="radio-group">
          <label className="radio-label">
            <input
              type="radio"
              value="user"
              checked={role === 'user'}
              onChange={() => setRole('user')}
            />
            User
          </label>
          <label className="radio-label">
            <input
              type="radio"
              value="admin"
              checked={role === 'admin'}
              onChange={() => setRole('admin')}
            />
            Admin
          </label>
        </div>
        {/* Username and password fields */}
        <div className="form-group">
          <input
            type="text"
            placeholder="Enter your Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="form-group">
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button type="submit">Login</button>
        {errorMessage && <div className="error-message">{errorMessage}</div>}
      </form>
      
    </div>
  );
};

export default Login;
