import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../css/RegistrationForm.css';
import axios from 'axios';

const Register = () => {
  // ... (existing code)

  return (
    <div className="registration-form-container">
      <form className="registration-form" onSubmit={handleSubmit}>
        <h2>Register</h2>
        {errorMessage && <div className="error-message">{errorMessage}</div>}
        <div className="form-group">
          <input
            type="text"
            placeholder="Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          {/* Helpful line */}
          <div className="helpful-line">Enter your full name</div>
        </div>
        <div className="form-group">
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          {/* Helpful line */}
          <div className="helpful-line">Enter your email address</div>
        </div>
        <div className="form-group">
          <input
            type="text"
            placeholder="Mobile Number"
            value={mobilenumber}
            onChange={(e) => setMobilenumber(e.target.value)}
          />
          {/* Helpful line */}
          <div className="helpful-line">Enter your 10-digit mobile number</div>
        </div>
        <div className="form-group">
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          {/* Helpful line */}
          <div className="helpful-line">
            Password must be at least 6 characters long, contain one uppercase letter, one special character, and no spaces.
          </div>
        </div>
        <div className="form-group">
          <input
            type="password"
            placeholder="Confirm Password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
          />
          {/* Helpful line */}
          <div className="helpful-line">Re-enter your password to confirm</div>
        </div>
        <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default Register;
