import React, { useState } from 'react';
import axios from 'axios';

const OTPVerification = ({ email }) => {
  const [otp, setOTP] = useState('');
  const [verificationFailed, setVerificationFailed] = useState(false);

  const handleVerifyOTP = async () => {
    try {
      const response = await axios.post('http://localhost:8080/verify-otp', {
        email,
        otp,
      });

      if (response.data.success) {
        // OTP verification successful, proceed with registration
        console.log('OTP verified successfully');
      } else {
        setVerificationFailed(true);
      }
    } catch (error) {
      console.error('Error verifying OTP:', error);
      setVerificationFailed(true);
    }
  };

  return (
    <div>
      <h2>OTP Verification</h2>
      <p>An OTP has been sent to your email. Please enter the OTP below:</p>
      <input
        type="text"
        placeholder="Enter OTP"
        value={otp}
        onChange={(e) => setOTP(e.target.value)}
      />
      {verificationFailed && <p className="error">Invalid OTP. Please try again.</p>}
      <button onClick={handleVerifyOTP}>Verify OTP</button>
    </div>
  );
};

export default OTPVerification;
